contracts/HostAbstract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaHostAbstractContract
Description: An on-chain  abstract Host contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;


abstract contract MecaHostAbstractContract {
    /// The owner of the contract
    address payable public owner;
    /// The register host fee
    uint256 immutable public HOST_REGISTER_FEE;
    /// The task register fee
    uint256 immutable public TASK_REGISTER_FEE;
    /// The initial stake for a host
    uint256 immutable public HOST_INITIAL_STAKE;
    /// The failed task fee
    uint256 immutable public FAILED_TASK_PENALTY;
    /// The scheduler contract address
    address public schedulerContractAddress;

    /**
    * @notice The Task information from a host
    * @param blockTimeout The block timeout of the task
    * @param fee The fee of the task
    */
    struct HostTask {
        uint256 blockTimeout;
        uint256 fee;
    }

    /**
    * @notice The Host structure
    * @param owner The owner of the host
    * @param eccPublicKey The ECC public key of the host
    * @param blockTimeoutLimit The block timeout limit of the host
    * @param stake The stake of the host
    */
    struct Host {
        address payable owner;
        bytes32[2] eccPublicKey;
        uint256 blockTimeoutLimit;
        uint256 stake;
    }

    // custom modifiers
    /**
    * @notice The onlyOwner modifier
    */
    modifier onlyOwner() {
        require(msg.sender == owner, "Owner only");
        _;
    }
    /**
    * @notice The hasStake modifier
    */
    modifier hasStake() {
        require(msg.value >= HOST_INITIAL_STAKE, "Minimum stake required");
        _;
    }
    /**
    * @notice The hasFee modifier
    */
    modifier hasFee() {
        require(msg.value == HOST_REGISTER_FEE, "Minimum fee required");
        _;
    }
    /**
    * @notice The hasFee modifier
    */
    modifier hasTaskFee() {
        require(msg.value == TASK_REGISTER_FEE, "Minimum task fee required");
        _;
    }
    /**
    * @notice The onlyScheduler modifier
    */
    modifier onlyScheduler() {
        require(msg.sender == schedulerContractAddress, "Scheduler only");
        _;
    }

    /**
    * @notice The constructor
    * @param hostRegisterFee The fee for registering a host
    * @param hostInitialStake The initial stake for a host
    * @param failedTaskPenalty The penalty for a failed task
    * @param taskRegisterFee The fee for registering a task
    */
    constructor(
        uint256 hostRegisterFee,
        uint256 hostInitialStake,
        uint256 failedTaskPenalty,
        uint256 taskRegisterFee
    ) {
        owner = payable(tx.origin);
        HOST_REGISTER_FEE = hostRegisterFee;
        HOST_INITIAL_STAKE = hostInitialStake;
        FAILED_TASK_PENALTY = failedTaskPenalty;
        TASK_REGISTER_FEE = taskRegisterFee;
        schedulerContractAddress = address(0);
    }

    // receive function

    /**
    * @notice The receive function
    */
    receive() external payable {
        revert();
    }

    // fallback function

    /**
    * @notice The fallback function
    */
    fallback() external payable {
        revert();
    }

    /**
    * @notice The clear function
    * @dev This function is used to clear the contract
    */
    function clear() external onlyOwner {
        _clear();
    }
    
    // External functions

    /**
    * @notice The registerAsHost function
    * @param publicKey The ecc public key of the host
    * @param blockTimeoutLimit The block timeout limit of the host
    */
    function registerAsHost(
        bytes32[2] calldata publicKey,
        uint256 blockTimeoutLimit
    )
        external
        payable
        hasStake
    {
        _addHost(
            Host({
                owner: payable(msg.sender),
                eccPublicKey: publicKey,
                blockTimeoutLimit: blockTimeoutLimit,
                stake: msg.value
            })
        );
    }

    /**
    * @notice The addStake function
    * @dev This function is used to add stake to the host
    */
    function addStake()
        external
        payable
    {
        _addStake(msg.sender, msg.value);
    }


    /**
    * @notice The widthdrawStake function
    * @param amount The amount to widthdraw
    */
    function widthdrawStake(
        uint256 amount
    )
        external
    {
        require(
            (_getHost(msg.sender).stake >= amount) &&
            (_getHost(msg.sender).stake - amount >= HOST_INITIAL_STAKE),
            "Invalid amount"
        );
        _removeStake(msg.sender, amount);
        payable(msg.sender).transfer(amount);
    }

    /**
    * @notice The updateBlockTimeoutLimit function
    * @param newBlockTimeoutLimit The new block timeout limit
    */
    function updateBlockTimeoutLimit(
        uint256 newBlockTimeoutLimit
    )
        external
    {
        _updateBlockTimeoutLimit(msg.sender, newBlockTimeoutLimit);
    }

    /**
    * @notice The updatePublicKey function
    * @param newPublicKey The new ecc public key
    */
    function updatePublicKey(
        bytes32[2] calldata newPublicKey
    )
        external
    {
        _updatePublicKey(msg.sender, newPublicKey);
    }

    /**
    * @notice The deleteHost function
    */
    function deleteHost()
        external
    {
        Host memory host = _getHost(msg.sender);
        require(host.blockTimeoutLimit == 0, "Host can get tasks");

        // TODO: verify of any active tasks

        uint256 stake = _deleteHost(msg.sender);
        payable(msg.sender).transfer(stake);
    }

    /**
    * @notice The setSchedulerContractAddress function
    * @param newSchedulerContractAddress The new scheduler contract address
    */
    function setSchedulerContractAddress(
        address newSchedulerContractAddress
    )
        external
        onlyOwner
    {
        schedulerContractAddress = newSchedulerContractAddress;
    }


    /**
    * @notice penalty for failed task
    * @param hostAddress The address of the host
    * @param userAddress The address of the user
    * @dev This function is used to penalize the host for a failed task
    * and send the penalty to the user
    */
    function penaltyForFailedTask(
        address hostAddress,
        address userAddress
    ) 
        external 
        onlyScheduler 
    {
        _removeStake(hostAddress, FAILED_TASK_PENALTY);
        payable(userAddress).transfer(FAILED_TASK_PENALTY);
    }

    /**
    * @notice Add a task to the host
    * @param ipfsSha256 The IPFS hash of the task
    * @param blockTimeout The block timeout of the task
    * @param fee The fee of the task
    */
    function addTask(
        bytes32 ipfsSha256,
        uint256 blockTimeout,
        uint256 fee
    )
        external
        payable
        hasTaskFee
    {
        _addTask(
            msg.sender,
            ipfsSha256,
            HostTask({
                blockTimeout: blockTimeout,
                fee: fee
            })
        );
    }

    /**
    * @notice The updateTaskBlockTimeout function
    * @param ipfsSha256 The IPFS hash of the task
    * @param newBlockTimeout The new block timeout of the task
    */
    function updateTaskBlockTimeout(
        bytes32 ipfsSha256,
        uint256 newBlockTimeout
    )
        external
    {
        _updateTaskBlockTimeout(msg.sender, ipfsSha256, newBlockTimeout);
    }

    /**
    * @notice The update task fee function
    * @param ipfsSha256 The IPFS hash of the task
    * @param newFee The new fee of the task
    */
    function updateTaskFee(
        bytes32 ipfsSha256,
        uint256 newFee
    )
        external
    {
        _updateTaskFee(msg.sender, ipfsSha256, newFee);
    }

    /**
    * @notice The deleteTask function
    * @param ipfsSha256 The IPFS hash of the task
    */
    function deleteTask(
        bytes32 ipfsSha256
    )
        external
    {
        _deleteTask(msg.sender, ipfsSha256);
        payable(msg.sender).transfer(TASK_REGISTER_FEE);
    }

    // External functions that are view

    /**
    * @notice The get the host public key
    * @param hostAddress The address of the host
    * @return bytes32[2] The public key of the host
    */
    function getHostPublicKey(
        address hostAddress
    )
        external
        view
        returns (bytes32[2] memory)
    {
        return _getHost(hostAddress).eccPublicKey;
    }

    /**
    * @notice The get the host block timeout limit
    * @param hostAddress The address of the host
    * @return uint256 The block timeout limit of the host
    */
    function getHostBlockTimeoutLimit(
        address hostAddress
    )
        external
        view
        returns (uint256)
    {
        return _getHost(hostAddress).blockTimeoutLimit;
    }

    /**
    * @notice The get the host stake
    * @param hostAddress The address of the host
    * @return uint256 The stake of the host
    */
    function getHostStake(
        address hostAddress
    )
        external
        view
        returns (uint256)
    {
        return _getHost(hostAddress).stake;
    }

    /**
    * @notice The get the hosts
    * @return Host[] The hosts
    */
    function getHosts(
    )
        external
        view
        returns (Host[] memory)
    {
        return _getHosts();
    }

    /**
    * @notice The get the task block timeout
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @return uint256 The block timeout of the task
    */
    function getTaskBlockTimeout(
        address hostAddress,
        bytes32 ipfsSha256
    )
        external
        view
        returns (uint256)
    {
        return _getTask(hostAddress, ipfsSha256).blockTimeout;
    }

    /**
    * @notice The get the task fee
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @return uint256 The fee of the task
    */
    function getTaskFee(
        address hostAddress,
        bytes32 ipfsSha256
    )
        external
        view
        returns (uint256)
    {
        return _getTask(hostAddress, ipfsSha256).fee;
    }
    
    // External functions that are pure

    // Public functions

    // Internal functions

    /**
    * @notice The clear function
    */
    function _clear() internal virtual;

    /**
    * @notice The addHost function
    * @param host The host to be added
    */
    function _addHost(
        Host memory host
    ) internal virtual;

    /**
    * @notice The addStake function
    * @param hostAddress The address of the host
    * @param stake The stake to be added
    */
    function _addStake(
        address hostAddress,
        uint256 stake
    ) internal virtual;

    /**
    * @notice The removeStake function
    * @param hostAddress The address of the host
    * @param stake The stake to be removed
    */
    function _removeStake(
        address hostAddress,
        uint256 stake
    ) internal virtual;

    /**
    * @notice The updateBlockTimeoutLimit function
    * @param hostAddress The address of the host
    * @param newBlockTimeoutLimit The new block timeout limit
    */
    function _updateBlockTimeoutLimit(
        address hostAddress,
        uint256 newBlockTimeoutLimit
    ) internal virtual;

    /**
    * @notice The updatePublicKey function
    * @param hostAddress The address of the host
    * @param newPublicKey The new public key
    */
    function _updatePublicKey(
        address hostAddress,
        bytes32[2] calldata newPublicKey
    ) internal virtual;

    /**
    * @notice The deleteHost function
    * @param hostAddress The address of the host
    */
    function _deleteHost(
        address hostAddress
    ) internal virtual returns (uint256);

    /**
    * @notice The addTask function
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @param task The task to be added
    */
    function _addTask(
        address hostAddress,
        bytes32 ipfsSha256,
        HostTask memory task
    ) internal virtual;

    /**
    * @notice The updateTaskBlockTimeout function
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @param newBlockTimeout The new block timeout of the task
    */
    function _updateTaskBlockTimeout(
        address hostAddress,
        bytes32 ipfsSha256,
        uint256 newBlockTimeout
    ) internal virtual;

    /**
    * @notice The updateTaskFee function
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @param newFee The new fee of the task
    */
    function _updateTaskFee(
        address hostAddress,
        bytes32 ipfsSha256,
        uint256 newFee
    ) internal virtual;

    /**
    * @notice The deleteTask function
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    */
    function _deleteTask(
        address hostAddress,
        bytes32 ipfsSha256
    ) internal virtual;

    // Internal functions that are view

    /**
    * @notice The get the host
    * @param hostAddress The address of the host
    * @return Host The host
    */
    function _getHost(
        address hostAddress
    )
        internal
        view
        virtual
        returns (Host memory);
    
    /**
    * @notice The get the hosts
    * @return Host[] The hosts
    */
    function _getHosts(
    )
        internal
        view
        virtual
        returns (Host[] memory);

    /**
    * @notice The get the task
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @return HostTask The task
    */
    function _getTask(
        address hostAddress,
        bytes32 ipfsSha256
    )
        internal
        view
        virtual
        returns (HostTask memory);
    // Private functions

}

contracts/HostContract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaHostContract
Description: An on-chain Host contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;

import "./HostAbstract.sol";

contract MecaHostContract is MecaHostAbstractContract {
    // host -> task_ipfsSha256 -> task_fees
    mapping(address => mapping(bytes32 => HostTask)) hostTasks;

    // We have a list with all the hosts and a mapping to check if a host exists
    // and what is the position in the list of a host (if it exists is index + 1, if not is 0)
    mapping(address => uint32) public hostsIndex;
    Host[] public hosts;

    // custom modifiers

    // constructor

    constructor(
        uint256 hostRegisterFee,
        uint256 hostInitialStake,
        uint256 failedTaskPenalty,
        uint256 taskRegisterFee
    )
        MecaHostAbstractContract(
            hostRegisterFee,
            hostInitialStake,
            failedTaskPenalty,
            taskRegisterFee
        )
    {
    }

    // External functions

    // External functions that are view
    
    // External functions that are pure

    // Public functions

    // Internal functions

    function _clear() internal override {
    }

    function _addHost(
        Host memory host
    ) internal override {
        if (hostsIndex[host.owner] != 0) {
            revert();
        }
        hosts.push(host);
        hostsIndex[host.owner] = uint32(hosts.length);
    }

    function _addStake(
        address hostAddress,
        uint256 stake
    ) internal override {
        uint32 index = _getHostIndex(hostAddress);
        hosts[index - 1].stake += stake;
    }

    function _removeStake(
        address hostAddress,
        uint256 stake
    ) internal override {
        uint32 index = _getHostIndex(hostAddress);
        hosts[index - 1].stake -= stake;
    }

    function _updateBlockTimeoutLimit(
        address hostAddress,
        uint256 newBlockTimeoutLimit
    ) internal override {
        uint32 index = _getHostIndex(hostAddress);
        hosts[index - 1].blockTimeoutLimit = newBlockTimeoutLimit;
    }

    function _updatePublicKey(
        address hostAddress,
        bytes32[2] calldata newPublicKey
    ) internal override {
        uint32 index = _getHostIndex(hostAddress);
        hosts[index - 1].eccPublicKey = newPublicKey;
    }

    function _deleteHost(
        address hostAddress
    ) internal override  returns (uint256) {
        uint32 index = _getHostIndex(hostAddress);
        uint256 toPay = hosts[index - 1].stake;
        hosts[index - 1] = hosts[hosts.length - 1];
        hostsIndex[hosts[index - 1].owner] = index;
        hostsIndex[hostAddress] = 0;
        hosts.pop();
        return toPay;
    }

    function _addTask(
        address hostAddress,
        bytes32 ipfsSha256,
        HostTask memory task
    ) internal override {
        if (hostTasks[hostAddress][ipfsSha256].blockTimeout != 0) {
            revert();
        }
        hostTasks[hostAddress][ipfsSha256] = task;
    }

    function _updateTaskBlockTimeout(
        address hostAddress,
        bytes32 ipfsSha256,
        uint256 newBlockTimeout
    ) internal override {
        hostTasks[hostAddress][ipfsSha256].blockTimeout = newBlockTimeout;
    }

    function _updateTaskFee(
        address hostAddress,
        bytes32 ipfsSha256,
        uint256 newFee
    ) internal override {
        hostTasks[hostAddress][ipfsSha256].fee = newFee;
    }

    function _deleteTask(
        address hostAddress,
        bytes32 ipfsSha256
    ) internal override {
        delete hostTasks[hostAddress][ipfsSha256];
    }

    // Internal functions that are view

    function _getHost(
        address hostAddress
    )
        internal
        view
        override
        returns (Host memory)
    {
        uint32 index = _getHostIndex(hostAddress);
        return hosts[index - 1];
    }

    function _getHosts(
    )
        internal
        view
        override
        returns (Host[] memory)
    {
        return hosts;
    }

    function _getTask(
        address hostAddress,
        bytes32 ipfsSha256
    )
        internal
        view
        override
        returns (HostTask memory)
    {
        return hostTasks[hostAddress][ipfsSha256];
    }

    // Internal functions that are pure

    // Private functions

    function _getHostIndex(
        address hostAddress
    ) private view returns (uint32) {
        uint32 index = hostsIndex[hostAddress];
        require(index != 0, "Host does not exist");
        return index;
    }

}

contracts/MecaContract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaDaoContract
Description: An on-chain DAO contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;


import "./SchedulerAbstract.sol";

contract MecaDaoContract
{
    /// The owner of the contract
	address payable public owner;
    /// The scheduler contract
    MecaSchedulerAbstractContract schedulerContract;

    // custom modifiers
    /**
    * @notice The onlyOwner modifier
    */
    modifier onlyOwner() {
        require(msg.sender == owner, "Owner only");
        _;
    }

    // constructor
    constructor() {
        owner = payable(msg.sender);
        schedulerContract = MecaSchedulerAbstractContract(payable(address(0)));
    }

    // receive function

    /**
    * @notice The receive function
    */
    receive() external payable {
        revert();
    }

    // fallback function

    /**
    * @notice The fallback function
    */
    fallback() external payable {
        revert();
    }

    // External functions

    /**
    * @notice The clear function
    */
    function clear() external onlyOwner {
        schedulerContract = MecaSchedulerAbstractContract(
            payable(address(0))
        );
        (bool success, ) = owner.call{value: address(this).balance}("");
        require(success, "Transfer failed.");
    }

    /**
    * @notice The setSchedulerContract function
    * @param newSchedulerContract The new scheduler contract
    */
    function setSchedulerContract(
        address newSchedulerContract
    ) external onlyOwner {
        schedulerContract = MecaSchedulerAbstractContract(
            payable(newSchedulerContract)
        );
    }

    // External functions that are view

    /**
    * @notice The getSchedulerContract function
    * @return address The scheduler contract adddress
    */
    function getSchedulerContract() external view returns (address) {
        return address(schedulerContract);
    }
    
    // External functions that are pure

    // Public functions

    // Internal functions

    // Internal functions that are view

    // Private functions



}

contracts/SchedulerAbstract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaSchedulerAbstractContract
Description: An on-chain  abstract Scheduler contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;

import "./TowerAbstract.sol";
import "./HostAbstract.sol";
import "./TaskAbstract.sol";


abstract contract MecaSchedulerAbstractContract
{
    /// The owner of the contract
    address payable public owner;
    /// The scheduler fee
    uint256 immutable public SCHEDULER_FEE;
    /// The host contract
    MecaHostAbstractContract internal hostContract;
    /// The task contract
    MecaTaskAbstractContract internal taskContract;
    /// The tower contract
    MecaTowerAbstractContract internal towerContract;
    /// The scheduler nonce
    uint256 public schedulerNonce;
    /// The scheduler flag
    bool public schedulerFlag;

    /**
    * @notice The RunningTaskFee structure
    * @param tower The tower fee
    * @param host The host fee
    * @param scheduler The scheduler fee
    * @param task The task fee
    * @param insurance The insurance fee
    */
    struct RunningTaskFee 
    {
        uint256 tower;
        uint256 host;
        uint256 scheduler;
        uint256 task;
        uint256 insurance;
    }

    /**
    * @notice The RunningTask structure
    * @param ipfsSha256 The IPFS hash of the task
    * @param inputHash The input hash of the task
    * @param size The size of the task
    * @param towerAddress The address of the tower
    * @param hostAddress The address of the host
    * @param owner The owner of the task
    * @param startBlock The start block of the task
    * @param blockTimeout The block timeout of the task
    * @param fee The fee of the task
    */
    struct RunningTask {
        bytes32 ipfsSha256;
        bytes32 inputHash;
        uint256 size;
        address towerAddress;
        address hostAddress;
        address owner;
        uint256 startBlock;
        uint256 blockTimeout;
        RunningTaskFee fee;
    }

    // custom modifiers
    /**
    * @notice The onlyOwner modifier
    */
    modifier onlyOwner() {
        require(msg.sender == owner, "Owner only");
        _;
    }
    /**
    * @notice The hasFee modifier
    */
    modifier hasFee() {
        require(msg.value == SCHEDULER_FEE, "Minimum fee required");
        _;
    }
    /**
    * @notice activeScheduler modifier
    */
    modifier activeScheduler() {
        require(schedulerFlag, "Scheduler not active");
        _;
    }

    /**
    * @notice The constructor
    * @param schedulerFee The fee for the scheduler
    */
    constructor(
        uint256 schedulerFee
    ) {
        owner = payable(tx.origin);
        SCHEDULER_FEE = schedulerFee;
        hostContract = MecaHostAbstractContract(payable(address(0)));
        taskContract = MecaTaskAbstractContract(payable(address(0)));
        towerContract = MecaTowerAbstractContract(payable(address(0)));
        schedulerNonce = 0;
        schedulerFlag = false;
    }

    // receive function

    /**
    * @notice The receive function
    */
    receive() external payable {
        revert();
    }

    // fallback function

    /**
    * @notice The fallback function
    */
    fallback() external payable {
        revert();
    }

    /**
    * @notice The clear function
    * @dev This function is used to clear the contract
    */
    function clear() external onlyOwner {
        _clear();
    }

    // External functions

    /**
    * @notice The setHostContract function
    * @param newHostContract The new host contract
    */
    function setHostContract(
        address newHostContract
    )
        external
        onlyOwner
    {
        hostContract = MecaHostAbstractContract(payable(newHostContract));
    }

    /**
    * @notice The setTaskContract function
    * @param newTaskContract The new task contract
    */
    function setTaskContract(
        address newTaskContract
    )
        external
        onlyOwner
    {
        taskContract = MecaTaskAbstractContract(payable(newTaskContract));
    }

    /**
    * @notice The setTowerContract function
    * @param newTowerContract The new tower contract
    */
    function setTowerContract(
        address newTowerContract
    )
        external
        onlyOwner
    {
        towerContract = MecaTowerAbstractContract(payable(newTowerContract));
    }

    /**
    * @notice The setSchedulerFlag function
    * @param newSchedulerFlag The new scheduler flag
    */
    function setSchedulerFlag(
        bool newSchedulerFlag
    )
        external
        onlyOwner
    {
        schedulerFlag = newSchedulerFlag;
    }

    /**
    * @notice Send a task to the scheduler
    * @param towerAddress The address of the tower
    * @param hostAddress The address of the host
    * @param ipfsSha256 The IPFS hash of the task
    * @param inputHash The hash of the input
    */
    function sendTask(
        address towerAddress,
        address hostAddress,
        bytes32 ipfsSha256,
        bytes32 inputHash
    )
        external
        payable
        activeScheduler
    {
        uint256 taskFee = taskContract.getTaskFee(ipfsSha256);
        uint256 taskSize = taskContract.getTaskSize(ipfsSha256);
        uint256 taskBlockTimeout = hostContract.getTaskBlockTimeout(
            hostAddress,
            ipfsSha256
        );
        uint256 towerFee = towerContract.getTowerFee(
            towerAddress,
            taskSize,
            taskBlockTimeout
        );
        uint256 hostFee = hostContract.getTaskFee(
            hostAddress,
            ipfsSha256
        );
        uint256 insuranceFee = (taskFee + hostFee + towerFee) / 10;
        uint256 totalFee = taskFee + hostFee + towerFee + SCHEDULER_FEE + insuranceFee;
        require(msg.value == totalFee, "Wrong funds");

        uint256 towerSizeLimit = towerContract.getTowerSizeLimit(
            towerAddress
        );
        uint256 hostBlockTimeoutLimit = hostContract.getHostBlockTimeoutLimit(
            hostAddress
        );

        uint256 hostFirstAvailableBlock = getHostFirstAvailableBlock(
            hostAddress
        );

        require(
            (hostFirstAvailableBlock + taskBlockTimeout) <= (block.number + hostBlockTimeoutLimit),
            "Host block timeout limit exceeded"
        );

        uint256 usedTowerSize = getTowerCurrentSize(towerAddress);

        require(
            (usedTowerSize + taskSize) <= towerSizeLimit,
            "Tower size limit exceeded"
        );

        bytes32 taskId = keccak256(
            abi.encodePacked(
                towerAddress,
                hostAddress,
                ipfsSha256,
                inputHash,
                schedulerNonce
            )
        );

        schedulerNonce += 1;

        _addRunningTask(
            taskId,
            RunningTask({
                ipfsSha256: ipfsSha256,
                inputHash: inputHash,
                size: taskSize,
                towerAddress: towerAddress,
                hostAddress: hostAddress,
                owner: msg.sender,
                startBlock: hostFirstAvailableBlock,
                blockTimeout: taskBlockTimeout,
                fee: RunningTaskFee({
                    tower: towerFee,
                    host: hostFee,
                    scheduler: SCHEDULER_FEE,
                    task: taskFee,
                    insurance: insuranceFee
                })
            })
        );
    }

    /**
    * @notice Finish a task
    * @param taskId The ID of the task
    */
    function finishTask(
        bytes32 taskId
    )
        external
    {
        RunningTask memory runningTask = _getRunningTask(taskId);
        require(
            msg.sender == runningTask.owner,
            "Only the owner can finish the task"
        );
        _deleteRunningTask(taskId);
        payable(runningTask.towerAddress).transfer(runningTask.fee.tower);
        payable(runningTask.hostAddress).transfer(runningTask.fee.host);
        payable(msg.sender).transfer(runningTask.fee.insurance);
        address taskOwner = taskContract.getTaskOwner(runningTask.ipfsSha256);
        payable(taskOwner).transfer(runningTask.fee.task);
    }

    // External functions that are view
    
    /**
    * @notice Get the host contract
    */
    function getHostContract() external view returns (address) {
        return address(hostContract);
    } 

    /**
    * @notice Get the task contract
    */
    function getTaskContract() external view returns (address) {
        return address(taskContract);
    }

    /**
    * @notice Get the tower contract
    */
    function getTowerContract() external view returns (address) {
        return address(towerContract);
    }
    
    // External functions that are pure

    // Public functions

    /**
    * @notice Get the host first available block
    * @param hostAddress The address of the host
    * @return uint256 The first available block
    */
    function getHostFirstAvailableBlock(
        address hostAddress
    )
        public
        view
        virtual
        returns (uint256);
    

    /**
    * @notice Get the tower current size
    * @param towerAddress The address of the tower
    * @return uint256 The current size of the tower
    */
    function getTowerCurrentSize(
        address towerAddress
    )
        public
        view
        virtual
        returns (uint256);

    // Internal functions

    /**
    * @notice The clear function
    */
    function _clear() internal virtual;

    /**
    * @notice The addRunningTask function
    * @param taskId The ID of the task
    * @param runningTask The running task
    */
    function _addRunningTask(
        bytes32 taskId,
        RunningTask memory runningTask
    )
        internal
        virtual;
    
    /**
    * @notice The deleteRunningTask function
    * @param taskId The ID of the task
    */
    function _deleteRunningTask(
        bytes32 taskId
    )
        internal
        virtual;

    // Internal functions that are view

    /**
    * @notice Get the running task
    * @param taskId The ID of the task
    * @return RunningTask The running task
    */
    function _getRunningTask(
        bytes32 taskId
    )
        internal
        view
        virtual
        returns (RunningTask memory);

    // Private functions

}

contracts/SchedulerContract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaSchedulerContract
Description: An on-chain Scheduler contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;

import "./SchedulerAbstract.sol";

contract MecaSchedulerContract is MecaSchedulerAbstractContract
{
    /// The running tasks map of taskId to running task
    mapping(bytes32 => RunningTask) public runningTasks;
    /// The Tower size map of towerAddress to size
    mapping(address => uint256) public towersSize;
    /// The end block of the hosts map of hostAddress to endBlock
    mapping(address => uint256) public hostsEndBlock;

    // custom modifiers

    // constructor
    constructor(
        uint256 schedulerFee
    ) MecaSchedulerAbstractContract(schedulerFee)
    {
    }

    // External functions

    // External functions that are view
    
    // External functions that are pure

    // Public functions

    function getHostFirstAvailableBlock(
        address hostAddress
    ) public view override returns (uint256)
    {
        uint256 hostFirstAvailableBlock = hostsEndBlock[hostAddress];
        if (hostFirstAvailableBlock < block.number) {
            hostFirstAvailableBlock = block.number;
        }
        return hostFirstAvailableBlock;
    }

    function getTowerCurrentSize(
        address towerAddress
    ) public view override returns (uint256)
    {
        return towersSize[towerAddress];
    }

    // Internal functions

    function _clear() internal override
    {
    }

    function _addRunningTask(
        bytes32 taskId,
        RunningTask memory runningTask
    ) internal override
    {
        runningTasks[taskId] = runningTask;
    }

    function _deleteRunningTask(
        bytes32 taskId
    ) internal override
    {
        towersSize[runningTasks[taskId].towerAddress] -= runningTasks[taskId].size;
        delete runningTasks[taskId];
    }

    // Internal functions that are view

    function _getRunningTask(
        bytes32 taskId
    ) internal view override returns (RunningTask memory)
    {
        return runningTasks[taskId];
    }

    // Private functions

}

contracts/TaskAbstract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaTaskAbstractContract
Description: An on-chain  abstract Task contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;


/**
* @title MecaTaskAbstractContract
* @author Ciocirlan Stefan-Dan (sdcioc)
* @notice The Task abstract contract for the MECA ecosystem
* @dev Give the Task structure and and
* external function for the Task contract
*/
abstract contract MecaTaskAbstractContract
{
    /// The owner of the contract
    address payable public owner;
    uint256 public TASK_ADDITION_FEE;

    /**
    * @notice The Task structure
    * @param ipfsSha256 The IPFS hash of the task
    * @param owner The owner of the task
    * @param fee The fee of the task
    * @param computingType The type of computing (0: cpu / 1: gpu)
    * @param size The i/o size of the task (in bytes)
    */
    struct Task {
        bytes32 ipfsSha256;
        address payable owner;
        uint256 fee;
        uint8 computingType;
        uint256 size;
    }

    // custom modifiers
    /**
    * @notice The onlyOwner modifier
    */
    modifier onlyOwner() {
        require(msg.sender == owner, "Owner only");
        _;
    }
    /**
    * @notice The onlyTaskOwner modifier
    * @param ipfsSha256 The IPFS hash of the task
    */
    modifier onlyTaskOwner(bytes32 ipfsSha256) {
        require(
            msg.sender == _getTask(ipfsSha256).owner,
            "Task owner only"
        );
        _;
    }

    /**
    * @notice The hasFee modifier
    */
    modifier hasFee() {
        require(msg.value == TASK_ADDITION_FEE, "Fee required");
        _;
    }

    /**
    * @notice The constructor
    * @param taskAdditionFee The fee for adding a task
    */
    constructor(uint256 taskAdditionFee) 
    {
        owner = payable(tx.origin);
        TASK_ADDITION_FEE = taskAdditionFee;
    }

    /**
    * @notice The receive function
    */
    receive() external payable {
        revert();
    }

    /**
    * @notice The fallback function
    */
    fallback() external payable {
        revert();
    }

    /**
    * @notice The clear function
    * @dev This function is used to clear the contract
    */
    function clear() external onlyOwner {
        _clear();
    }

    // External functions
    /**
    * @notice The addTask function
    * @dev This function is used to add a task to the contract
    * The function is payable and the fee is sent to the contract
    * and kept as a stake and it returns to the task owner when
    * the task is deleted
    * @param ipfsSha256 The IPFS hash of the task
    * @param fee The fee of the task
    * @param computingType The type of computing (0: cpu / 1: gpu)
    * @param size The i/o size of the task (in bytes)
    */
    function addTask(
        bytes32 ipfsSha256,
        uint256 fee,
        uint8 computingType,
        uint256 size
    )
        external
        payable
        hasFee
    {
        _addTask(
            Task({
                ipfsSha256: ipfsSha256,
                owner: payable(msg.sender),
                fee: fee,
                computingType: computingType,
                size: size
            })
        );
    }

    /**
    * @notice Updates the task fee
    * @param ipfsSha256 The IPFS hash of the task
    * @param newFee The new fee of the task
    */
    function updateTaskFee(
        bytes32 ipfsSha256,
        uint256 newFee
    )
        external
        onlyTaskOwner(ipfsSha256)
    {
        _updateTaskFee(ipfsSha256, newFee);
    }

    /**
    * @notice Updates the task owner
    * @param ipfsSha256 The IPFS hash of the task
    * @param newOwner The new owner of the task
    */
    function updateTaskOwner(
        bytes32 ipfsSha256,
        address newOwner
    )
        external
        onlyTaskOwner(ipfsSha256)
    {
        _updateTaskOwner(ipfsSha256, newOwner);
    }

    /**
    * @notice Updates the task i/o size
    * @param ipfsSha256 The IPFS hash of the task
    * @param newSize The new i/o size of the task
    */
    function updateTaskSize(
        bytes32 ipfsSha256,
        uint256 newSize
    )
        external
        onlyTaskOwner(ipfsSha256)
    {
        _updateTaskSize(ipfsSha256, newSize);
    }

    /**
    * @notice Deletes a task
    * @dev Return the task fee to the owner
    * @param ipfsSha256 The IPFS hash of the task
    */
    function deleteTask(
        bytes32 ipfsSha256
    )
        external
        onlyTaskOwner(ipfsSha256)
    {
        _deleteTask(ipfsSha256);
        _getTask(ipfsSha256).owner.transfer(TASK_ADDITION_FEE);
    }

    // View External functions
    /**
    * @notice Get the task fee for the task developer
    * @param ipfsSha256 The IPFS hash of the task
    * @return uint256 The fee of the task
    */
    function getTaskFee(
        bytes32 ipfsSha256
    )
        external
        view
        returns (uint256)
    {
        return _getTask(ipfsSha256).fee;
    }

    /**
    * @notice Get the task i/o size for the task
    * @param ipfsSha256 The IPFS hash of the task
    * @return uint256 The i/o size of the task
    */
    function getTaskSize(
        bytes32 ipfsSha256
    )
        external
        view
        returns (uint256)
    {
        return _getTask(ipfsSha256).size;
    }

    /**
    * @notice Get the task computing type for the task
    * @param ipfsSha256 The IPFS hash of the task
    * @return uint8 The computing type of the task
    */
    function getTaskComputingType(
        bytes32 ipfsSha256
    )
        external
        view
        returns (uint8)
    {
        return _getTask(ipfsSha256).computingType;
    }

    /**
    * @notice Get the task owner for the task
    * @param ipfsSha256 The IPFS hash of the task
    * @return address The owner of the task
    */
    function getTaskOwner(
        bytes32 ipfsSha256
    )
        external
        view
        returns (address)
    {
        return _getTask(ipfsSha256).owner;
    }

    /**
    * @notice Get all the tasks
    * @return task[] The tasks
    */
    function getTasks() external view virtual returns (Task[] memory);

    // Pure External functions

    // Public functions

    // Internal functions

    /**
    * @notice The _clear function
    * @dev This function is used to clear the contract storage
    */
    function _clear() internal virtual;

    /**
    * @notice The _addTask function
    * @dev This function is used to add a task to the contract
    * @param task The task to be added
    */
    function _addTask(
        Task memory task
    ) internal virtual;

    /**
    * @notice Updates the task fee
    * @param ipfsSha256 The IPFS hash of the task
    * @param newFee The new fee of the task
    */
    function _updateTaskFee(
        bytes32 ipfsSha256,
        uint256 newFee
    ) internal virtual;

    /**
    * @notice Updates the task owner
    * @param ipfsSha256 The IPFS hash of the task
    * @param newOwner The new owner of the task
    */
    function _updateTaskOwner(
        bytes32 ipfsSha256,
        address newOwner
    ) internal virtual;

    /**
    * @notice Updates the task i/o size
    * @param ipfsSha256 The IPFS hash of the task
    * @param newSize The new i/o size of the task
    */
    function _updateTaskSize(
        bytes32 ipfsSha256,
        uint256 newSize
    ) internal virtual;

    /**
    * @notice Deletes a task
    * @param ipfsSha256 The IPFS hash of the task
    */
    function _deleteTask(
        bytes32 ipfsSha256
    ) internal virtual;

    /**
    * @notice get the task given the ipfs hash
    * @param ipfsSha256 The IPFS hash of the task
    * @return Task The task
    */
    function _getTask(
        bytes32 ipfsSha256
    )
        internal
        view
        virtual
        returns (Task memory);

    // Private functions

}

contracts/TaskContract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaTaskContract
Description: An on-chain Task contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;

import "./TaskAbstract.sol";

contract MecaTaskContract is MecaTaskAbstractContract
{
    // We have a list with all the tasks and a mapping to check if a task exists
    // and what is the position in the list of a task (if it exists is index + 1, if not is 0)
    mapping(bytes32 => uint32) public tasks_index;
    Task[] public tasks;

    constructor(uint256 taskAdditionFee)
        MecaTaskAbstractContract(taskAdditionFee)
    {
    }

    // External functions

    // External functions that are view

    function getTasks() external view override returns (Task[] memory)
    {
        return tasks;
    }

    // External functions that are pure

    // Public functions

    // Internal functions
    function _clear() internal override
    {
        // TODO: maybe go through task as pops and delete them
        for (uint256 i = 0; i < tasks.length; i++) {
            tasks_index[tasks[i].ipfsSha256] = 0;
        }
        delete tasks;
    }

    function _addTask(
        Task memory task
    ) internal override {
        if (tasks_index[task.ipfsSha256] != 0) {
            revert();
        }
        tasks.push(task);
        tasks_index[task.ipfsSha256] = uint32(tasks.length);
    }

    function _updateTaskFee(
        bytes32 ipfsSha256,
        uint256 fee
    ) internal override {
        tasks[_getTaskIndex(ipfsSha256) - 1].fee = fee;
    }

    function _updateTaskOwner(
        bytes32 ipfsSha256,
        address newOwner
    ) internal override {
        tasks[_getTaskIndex(ipfsSha256) - 1].owner = payable(newOwner);
    }

    function _updateTaskSize(
        bytes32 ipfsSha256,
        uint256 size
    ) internal override {
        tasks[_getTaskIndex(ipfsSha256) - 1].size = size;
    }

    function _deleteTask(
        bytes32 ipfsSha256
    ) internal override {
        uint32 index = _getTaskIndex(ipfsSha256);
        tasks[index - 1] = tasks[tasks.length - 1];
        tasks_index[tasks[index - 1].ipfsSha256] = index;
        tasks_index[ipfsSha256] = 0;
        tasks.pop();
    }

    function _getTask(
        bytes32 ipfsSha256
    )
        internal
        view
        override
        returns (Task memory)
    {
        return tasks[_getTaskIndex(ipfsSha256) - 1];
    }

    // Private functions
    /**
    * @notice get the index of the task given the ipfs hash
    * @param ipfsSha256 The IPFS hash of the task
    * @return uint32 The index of the task
    */
    function _getTaskIndex(
        bytes32 ipfsSha256
    )
        private
        view
        returns (uint32)
    {
        uint32 index = tasks_index[ipfsSha256];
        require(index != 0, "Task not found");
        return index;
    }

}

contracts/TowerAbstract.sol.content:
// SPDX-License-Identifier: MIT 
/*

Name: MecaHostAbstractContract
Description: An on-chain  abstract Host contract for MECA ecosystem
Author: Ciocirlan Stefan-Dan (sdcioc)
Date: Feb 2024

MIT License

*/

pragma solidity ^0.8.17;

import "./SchedulerAbstract.sol";

abstract contract MecaTowerAbstractContract {
    /// The owner of the contract
    address payable public owner;
    /// The initial stake for adding a tower
    uint256 immutable public TOWER_INITIAL_STAKE;
    /// The fee for requesting to be host to a tower
    uint256 immutable public HOST_REQUEST_FEE;
    /// The penalty for failed task
    uint256 immutable public FAILED_TASK_PENALTY;
    /// The scheduler contract address
    address public schedulerContractAddress;

    /**
    * @notice The Tower structure
    * @param owner The owner of the tower
    * @param sizeLimit The size limit of the tower
    * @param publicConnection The public connection of the tower
    * @param feeType The type of the fee (0: fixed / 1: size / 2: time / 3: size and time)
    * @param fee The fee of the tower
    * @param stake The stake of the tower
    */
    struct Tower {
        address payable owner;
        uint256 sizeLimit;
        string publicConnection;
        uint8 feeType;
        uint256 fee;
        uint256 stake;
    }
    // custom modifiers
    /**
    * @notice The onlyOwner modifier
    */
    modifier onlyOwner() {
        require(msg.sender == owner, "Owner only");
        _;
    }
    /**
    * @notice The hasStake modifier
    */
    modifier hasStake() {
        require(msg.value >= TOWER_INITIAL_STAKE, "Minimum stake required");
        _;
    }
    /**
    * @notice The hasFee modifier
    */
    modifier hasFee() {
        require(msg.value == HOST_REQUEST_FEE, "Minimum fee required");
        _;
    }
    /**
    * @notice The onlyScheduler modifier
    */
    modifier onlyScheduler() {
        require(msg.sender == schedulerContractAddress, "Scheduler only");
        _;
    }

    // constructor

    /**
    * @notice The constructor
    * @param towerInitialStake The initial stake of a tower
    * @param hostRequestFee The fee for requesting to be host to a tower
    * @param failedTaskPenalty The penalty for failed task
    */
    constructor(
        uint256 towerInitialStake,
        uint256 hostRequestFee,
        uint256 failedTaskPenalty
    ) {
        owner = payable(tx.origin);
        TOWER_INITIAL_STAKE = towerInitialStake;
        HOST_REQUEST_FEE = hostRequestFee;
        FAILED_TASK_PENALTY = failedTaskPenalty;
        schedulerContractAddress = address(0);
    }

    // receive function

    /**
    * @notice The receive function
    */
    receive() external payable {
        revert();
    }

    // fallback function

    /**
    * @notice The fallback function
    */
    fallback() external payable {
        revert();
    }

    /**
    * @notice The clear function
    * @dev This function is used to clear the contract
    */
    function clear() external onlyOwner {
        _clear();
    }
    
    // External functions
    /**
    * @notice Set the scheduler contract address
    * @param newSchedulerContractAddress The new scheduler contract address
    * @dev This function is used only by the owner to set the scheduler contract address 
    */
    function setSchedulerContractAddress(
        address newSchedulerContractAddress
    ) 
        external 
        onlyOwner 
    {
        schedulerContractAddress = newSchedulerContractAddress;
    }

    /**
    * @notice penalty for failed task
    * @param towerAddress The address of the tower
    * @param userAddress The address of the user
    * @dev This function is used to penalize the tower for a failed task
    * and send the penalty to the user
    */
    function penaltyForFailedTask(
        address towerAddress,
        address userAddress
    ) 
        external 
        onlyScheduler 
    {
        _removeStake(towerAddress, FAILED_TASK_PENALTY);
        payable(userAddress).transfer(FAILED_TASK_PENALTY);
    }



    /**
    * @notice Register as a tower
    * @param sizeLimit The size limit of the tower
    * @param publicConnection The public connection of the tower
    * @param fee The fee of the tower
    * @param feeType The type of the fee (0: fixed / 1: size / 2: time / 3: size and time)
    * @dev This function is used to register as a tower the stake is inside the call
    */
    function registerAsTower(
        uint256 sizeLimit,
        string calldata publicConnection,
        uint256 fee,
        uint8 feeType
    ) 
        external
        payable
        hasStake
    {
        _registerAsTower(
            Tower({
                owner: payable(msg.sender),
                sizeLimit: sizeLimit,
                publicConnection: publicConnection,
                feeType: feeType,
                fee: fee,
                stake: msg.value
            })
        );

    }

    /**
    * @notice add a stake to the tower
    * @dev Only the tower can use this function for itself
    */
    function addStake() 
        external 
        payable 
    {
        _addStake(msg.sender, msg.value);
    }

    /**
    * @notice withdraw the money from the tower stake but
    * keeping the minimum stake
    * @dev Only the tower can use this function for itself
    * @param amount The amount to withdraw
    */
    function withdrawStake(
        uint256 amount
    ) 
        external 
    {
        // verify for overflows
        require(
            (_getTower(msg.sender).stake >=  amount) &&
            (_getTower(msg.sender).stake >= (TOWER_INITIAL_STAKE + amount)), "Minimum stake required");
        _removeStake(msg.sender, amount);
        payable(msg.sender).transfer(amount);
    }


    /**
    * @notice update the size limit of the tower
    * @dev Only the tower can use this function for itself
    */
    function updateSizeLimit(
        uint256 newSizeLimit
    ) 
        external 
    {
        _updateSizeLimit(msg.sender, newSizeLimit);
    }

    /**
    * @notice update the public connection of the tower
    * @dev Only the tower can use this function for itself
    */
    function updatePublicConnection(
        string calldata newPublicConnection
    ) 
        external 
    {
        _updatePublicConnection(msg.sender, newPublicConnection);
    }

    /**
    * @notice update the fee of the tower
    * @dev Only the tower can use this function for itself
    */
    function updateFee(
        uint8 newFeeType,
        uint256 newFee
    ) 
        external 
    {
        _updateFee(msg.sender, newFeeType, newFee);
    }

    /**
    * @notice delete the tower
    * @dev Only the tower can use this function for itself
    */
    function deleteTower() 
        external 
    {
        // check if the tower has the size limit 0
        Tower memory tower = _getTower(msg.sender);
        require(tower.sizeLimit == 0, "Size limit must be 0");
        // TODO: check if the tower has no running tasks
        // use schedulerContractAddress to check if the tower has no running tasks

        // compute the stake and delete the tower
        uint256 stake = tower.stake;
        uint256 pendingHosts = _deleteTower(msg.sender);
        stake += pendingHosts * HOST_REQUEST_FEE;

        // transfer the stake
        payable(msg.sender).transfer(stake);
    }

    /**
    * @notice register the sending host for the tower
    * @param towerAddress The address of the tower
    * @dev This function is used to register the sending host for the tower
    * The host must pay a fee
    */
    function registerMeForTower(
        address towerAddress
    ) 
        external
        payable
        hasFee
    {
        _registerHostForTower(towerAddress, msg.sender);
    }

    /**
    * @notice accept the host
    * @param hostAddress The address of the host
    * @dev This function is used to accept the host candidate
    * the tower gets the host fee
    */
    function acceptHost(
        address hostAddress
    ) 
        external
    {
        _acceptHost(msg.sender, hostAddress);
        payable(msg.sender).transfer(HOST_REQUEST_FEE);
    }

    /**
    * @notice reject the host
    * @param hostAddress The address of the host
    * @dev This function is used to reject the host candidate
    * the tower gets the host fee
    */
    function rejectHost(
        address hostAddress
    ) 
        external
    {
        _rejectHost(msg.sender, hostAddress);
        payable(msg.sender).transfer(HOST_REQUEST_FEE);
    }

    /**
    * @notice delete a host from the tower
    * @param hostAddress The address of the host
    * @dev This function is used to delete a host from the tower
    */
    function deleteHost(
        address hostAddress
    ) 
        external
    {
        _deleteHost(msg.sender, hostAddress);
    }

    // External functions that are view
    /**
    * @notice Get the tower size limit
    * @param towerAddress The address of the tower
    * @return uint256 The size limit of the tower
    */
    function getTowerSizeLimit(
        address towerAddress
    ) 
        external 
        view 
        returns (uint256)
    {
        return _getTower(towerAddress).sizeLimit;
    }

    /**
    * @notice Get the tower public connection
    * @param towerAddress The address of the tower
    * @return string The public connection of the tower
    */
    function getTowerPublicConnection(
        address towerAddress
    ) 
        external 
        view 
        returns (string memory)
    {
        return _getTower(towerAddress).publicConnection;
    }

    /**
    * @notice Get the tower fee
    * @param towerAddress The address of the tower
    * @param size The size of the task
    * @param blockTimeoutLimit The block timeout limit of the task
    * @return uint256 The fee of the tower
    */
    function getTowerFee(
        address towerAddress,
        uint256 size,
        uint256 blockTimeoutLimit
    ) 
        external 
        view 
        returns (uint256)
    {
        Tower memory tower = _getTower(towerAddress);
        uint8 feeType = tower.feeType;
        uint256 fee = tower.fee;
        if (feeType == 0) {
            return fee;
        } else if (feeType == 1) {
            return fee * size;
        } else if (feeType == 2) {
            return fee * blockTimeoutLimit;
        } else if (feeType == 3) {
            return fee * size * blockTimeoutLimit;
        } else {
            return 0;
        }
    }

    /**
    * @notice Get the tower stake
    * @param towerAddress The address of the tower
    * @return uint256 The stake of the tower
    */
    function getTowerStake(
        address towerAddress
    ) 
        external 
        view 
        returns (uint256)
    {
        return _getTower(towerAddress).stake;
    }


    /**
    * @notice Get the tower hosts
    * @param towerAddress The address of the tower
    * @return address[] The hosts of the tower
    */
    function getTowerHosts(
        address towerAddress
    ) 
        external 
        view 
        returns (address[] memory)
    {
        return _getTowerHosts(towerAddress);
    }

    /**
    * @notice Get the tower pending hosts
    * @param towerAddress The address of the tower
    * @return address[] The pending hosts of the tower
    */
    function getTowerPendingHosts(
        address towerAddress
    ) 
        external 
        view 
        returns (address[] memory)
    {
        return _getTowerPendingHosts(towerAddress);
    }

    /**
    * @notice Get the towers
    * @return tower[] The towers
    */
    function getTowers(
    ) 
        external 
        view 
        returns (Tower[] memory)
    {
        return _getTowers();
    }
    
    // External functions that are pure

    // Public functions

    // Internal functions

    /**
    * @notice The clear function
    */
    function _clear() internal virtual;

    /**
    * @notice register a tower
    * @param tower The tower to register
    */
    function _registerAsTower(
        Tower memory tower
    ) internal virtual;

    /**
    * @notice add a stake to the tower
    * @param towerAddress The address of the tower
    * @param stake The stake to add
    */
    function _addStake(
        address towerAddress,
        uint256 stake
    ) internal virtual;

    /**
    * @notice remove the money from the tower stake
    * @param towerAddress The address of the tower
    * @param amount The amount to withdraw
    */
    function _removeStake(
        address towerAddress,
        uint256 amount
    ) internal virtual;

    /**
    * @notice update the size limit of the tower
    * @param towerAddress The address of the tower
    * @param newSizeLimit The new size limit
    */
    function _updateSizeLimit(
        address towerAddress,
        uint256 newSizeLimit
    ) internal virtual;

    /**
    * @notice update the public connection of the tower
    * @param towerAddress The address of the tower
    * @param newPublicConnection The new public connection
    */
    function _updatePublicConnection(
        address towerAddress,
        string calldata newPublicConnection
    ) internal virtual;


    /**
    * @notice update the fee of the tower
    * @param towerAddress The address of the tower
    * @param newFeeType The new fee type
    * @param newFee The new fee
    */
    function _updateFee(
        address towerAddress,
        uint8 newFeeType,
        uint256 newFee
    ) internal virtual;

    /**
    * @notice delete the tower
    * @param towerAddress The address of the tower
    */
    function _deleteTower(
        address towerAddress
    ) internal virtual returns (uint256);

    /**
    * @notice register the sending host for the tower
    * @param towerAddress The address of the tower
    * @param hostAddress The address of the host
    */
    function _registerHostForTower(
        address towerAddress,
        address hostAddress
    ) internal virtual;

    /**
    * @notice accept the host
    * @param towerAddress The address of the tower
    * @param hostAddress The address of the host
    */
    function _acceptHost(
        address towerAddress,
        address hostAddress
    ) internal virtual;

    /**
    * @notice reject the host
    * @param towerAddress The address of the tower
    * @param hostAddress The address of the host
    */
    function _rejectHost(
        address towerAddress,
        address hostAddress
    ) internal virtual;

    /**
    * @notice delete a host from the tower
    * @param towerAddress The address of the tower
    * @param hostAddress The address of the host
    */
    function _deleteHost(
        address towerAddress,
        address hostAddress
    ) internal virtual;

    // Internal functions that are view

    /**
    * @notice Get the tower
    * @param towerAddress The address of the tower
    * @return Tower The tower
    */
    function _getTower(
        address towerAddress
    ) 
        internal 
        view 
        virtual 
        returns (Tower memory);
    
    /**
    * @notice Get the tower hosts
    * @param towerAddress The address of the tower
    * @return address[] The hosts of the tower
    */
    function _getTowerHosts(
        address towerAddress
    ) 
        internal 
        view 
        virtual 
        returns (address[] memory);

    
    /**
    * @notice Get the tower pending hosts
    * @param towerAddress The address of the tower
    * @return address[] The pending hosts of the tower
    */
    function _getTowerPendingHosts(
        address towerAddress
    ) 
        internal 
        view 
        virtual 
        returns (address[] memory);
    
    /**
    * @notice Get the towers
    * @return tower[] The towers
    */
    function _getTowers(
    ) 
        internal 
        view 
        virtual 
        returns (Tower[] memory);

    // Private functions
}

